function App() {
  let numOfTavellers = 3;
  const [state, setState] = React.useState({
      metadata: {
          travellers: []
      }
})

return (
  <div className="App">
      <p>{JSON.stringify(state)}</p>
      {Array(numOfTavellers).fill(0).map((_, i) =>
          <div>
              <input type="text" id={`traveller ${i+1}`} key={i} onChange={
                  ({target}) => {
                      let traveller = [...state?.metadata?.travellers].find((t) => t.id == i)
                      let newList = [...state?.metadata?.travellers].filter((t) => t.id !== i)
                      if(traveller) {
                          newList.push({...traveller, phone: target.value})
                      } else {
                          newList.push({id: i, phone: target.value})
                      }
                      setState({...state, metadata: {
                              ...state?.metadata,
                              travellers: newList
                          }})
                      console.log(state)
                  }
              } placeholder={`phone number traveller${i}`}/>
              <input type="text" placeholder={"name"} onChange={({target}) => {
                  let traveller = [...state?.metadata?.travellers].find((t) => t.id == i)
                  let newList = [...state?.metadata?.travellers].filter((t) => t.id !== i)
                  if(traveller) {
                      newList.push({...traveller, passport: {name: target.value}})
                  } else {
                      newList.push({id: i, passport: {name: target.value}})
                  }

                  setState({...state, metadata: {
                          ...state?.metadata,
                          travellers: newList
                      }})
              }}/>
          </div>
      )}

  </div>
);
}

export default App;
