export const UserDetailsContext = createContext()

const App = () => {

  const [userDetails] = useState({
    name: "Stephen Wayar",
    age: 21
  })

  return(
    <UserDetailsContext.Provider value={userDetails}>
      <ChildComp userDetails={userDetails}/>
    </UserDetailsContext.Provider>
  )
}

