import React from "react"
import "./successNotification.styles.css"

const SuccessNotification = ({ message, notification }) => {

  if (message === null) {
    return null
  }

  const timeout = () => {
    return "show"
    .then(setTimeout(() => "hide", 2000))
  }


  return (
    <div className={`success ${notification === null ? "hide" : timeout}`}>
      {message}
    </div>
  )
}
export default SuccessNotification
