import React, {useEffect, useState} from "react";
import axios from "axios";

const Weather = ({capitalCity, temperature, wind}) => {

  const url = `https://api.openweathermap.org/data/2.5/weather?q=abuja&appid=${process.env.REACT_APP_WEATHER_API_KEY}`

  useEffect(() => {

    axios

      .get(url)

      .then((res) => {

        console.log("I passed weather API")

        console.log(res.data)

      })

      .catch(err => console.log)
  })

  return(
    <div>
      <p>
        <b>Weather in {capitalCity}</b>
      </p>

      <p>
        <b>temparature: </b> {temperature}
      </p>

      <p>
        <b>wind: </b> {wind}
      </p>
    </div>
  )
}
export default Weather
