  const addNote = (e) => {
    e.preventDefault()
    setNotes(e.target.value)
  }

  const handleChange = (e) => {
    setVals()
  }
  console.log(notes)

  return (
    <div>
      <h1>Notes</h1>
      <ul>
        {notes.map((note, index) => 
            <Note key={index} note={note} />
        )}
      </ul>   

      <form onSubmit={addNote}>
        <input onChange={handleChange}/>
        <button type="submit">save</button>
      </form>  
    </div>
  )
}

