import React, { useEffect, useState } from "react";
import Result from "./components/Result.component";
import axios from "axios";

const App = () => {

  const [countries, setCountries] = useState([])
  const [search, setSearch] = useState("")

  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then(res => {
      console.log("Promise passed!");
      const data = res.data.map(datum => (datum.name.common))
      setCountries(data)
    })
  }, [])

  const handleSearch = e => {
    setSearch(e.target.value)
  }

  const filteredResult = countries.filter(country => country.includes(search)).map((filtered, index) => (<Result key={index} result={filtered}/>))

  return(
    <div>
      <label>Find countries:</label>
      <input value={search} onChange={handleSearch}/>
      {search.length > 0 ? filteredResult : ""}
    </div>
  )
}
export default App;
