import React, { useEffect, useState } from "react";
import SearchResult from "./components/searchResult.component";
import axios from "axios";

const App = () => {

  const [allCountries, setAllCountries] = useState([])
  const [countries, setCountries] = useState([])
  const [newFilter, setNewFilter] = useState("")

  useEffect(() => {
    axios.get("https://restcountries.com/v3.1/all").then(res => {
      setAllCountries(res.data)
      console.log("Promise passed!")
    })
  }, [])

  const handleSearch = e => {
    setNewFilter(e.target.value)
    console.log(newFilter);
    if (newFilter) {
      const regex = new RegExp( newFilter, 'i' );
      const filteredCountries = () => allCountries.filter(country => country.name.match(regex))
      setCountries(filteredCountries)
      console.log(filteredCountries);
    }
  }

  return(
    <div>
      <label><b>Find countires:</b></label>
      <input value={newFilter} onChange={handleSearch}/>
    </div>
  )
}
export default App;
